import type { AppProps } from "next/app";
import "../styles/globals.css";
import { useState } from "react";
import Banner from "../components/Banner";

export default function App({ Component, pageProps }: AppProps) {
  const [nav, setNav] = useState(false);
  const [navBackground, setNavBackground] = useState(false);
  const [some, setSome] = useState(false);

  const handleClick = () => {
    if (nav) setNav(false);
    else setNav(true);
  };

  const changeBackground = () => {
    if (window.scrollY >= 10) setNavBackground(true);
    else setNavBackground(false);
  };

  if (typeof window !== "undefined")
    window.addEventListener("scroll", changeBackground);

  return (
    <div>
      <nav
        className={`${
          some ? "text-green-600" : "text-purple-600"
        } w-screen fixed`}
      >
        <div>yes</div>
        {/* TODO: Fix how the background and text color logic works */}
        <div
          className={`${
            navBackground || nav ? "pt-3 bg-white" : "pt-5 lg:pt-10"
          } ${
            nav ? "pt-3 bg-white" : "pt-5 lg:pt-10"
          } flex justify-between items-center px-20 pb-3 lg:justify-center`}
        >
          {/* Title */}
          <div>
            <a
              href="/"
              className={`${
                navBackground ? "text-2xl" : "text-white text-4xl"
              } font-bold`}
            >
              Jevelin
            </a>
          </div>

          {/* Burger menu */}
          <div className="lg:hidden">
            <button className="block" onClick={() => handleClick()}>
              {/* TODO Add some transition between X and burger */}
              {nav ? (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  width={32}
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                  className={`${
                    navBackground ? "" : "fill-current text-white"
                  }`}
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M6 18L18 6M6 6l12 12"
                  />
                </svg>
              ) : (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  width={32}
                  stroke="currentColor"
                  className={`${
                    navBackground ? "" : "fill-current text-white"
                  }`}
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M4 6h16M4 12h16M4 18h16"
                  />
                </svg>
              )}
            </button>
          </div>
        </div>

        <div>
          {/* List */}
          {/* TODO ADD SOME BACKGROUND AND HOVER EFFECT */}
          <ul
            className={`${nav ? "block" : "hidden"} ${
              navBackground ? "" : "text-white"
            } lg:flex lg:justify-center font-bold uppercase pb-5`}
          >
            <li className="lg:px-5 block">
              <a href="#">Hjem</a>
            </li>
            <li className="lg:px-5 block">
              <a href="#">Om oss</a>
            </li>
            <li className="lg:px-5 block">
              <a href="#">Tilbud</a>
            </li>
            <li className="lg:px-5 block">
              <a href="#">Meny</a>
            </li>
            <li className="lg:px-5 block">
              <a href="#">Kontakt oss</a>
            </li>
          </ul>
        </div>
      </nav>
      <Banner />

      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
    </div>
  );
}
