import type { AppProps } from "next/app";
import "../styles/globals.css";
import { useState } from "react";
import Banner from "../components/Banner";

export default function App({ Component, pageProps }: AppProps) {
  const [navOpen, setNavOpen] = useState(false);
  const [scrolledDown, setScrolledDown] = useState(false);

  const handleClick = () => {
    if (navOpen) setNavOpen(false);
    else setNavOpen(true);
  };

  const changeBackground = () => {
    if (window.scrollY >= 10) setScrolledDown(true);
    else setScrolledDown(false);
  };

  if (typeof window !== "undefined")
    window.addEventListener("scroll", changeBackground);

  {
    /* TODO: Needs some logic for when navbar is opened and then the screen size is changed */
  }

  return (
    <div>
      <nav
        className={`${
          scrolledDown || navOpen ? "text-black" : "text-white"
        } w-screen fixed`}
      >
        {/* TODO: Fix how the background and text color logic works */}
        {/* Hør om meningen til mams på navbar ikke scrolled, men dropdown nede */}

        {/* Container for title and burger menu */}
        <div
          className={`
          ${scrolledDown && !navOpen ? "pt-3 bg-white" : "lg:pt-10"}
          ${navOpen && !scrolledDown ? "pt-5 pb-5 bg-white" : ""} 
          ${scrolledDown && navOpen ? "pt-3 bg-white" : ""}
          ${!scrolledDown && !navOpen ? "pt-5" : ""}

          flex justify-between items-center paddingx pb-3 lg:justify-center`}
        >
          {/* Title */}
          <div>
            <a
              href="/"
              className={`${scrolledDown ? "text-2xl" : "text-4xl"} font-bold`}
            >
              Jevelin
            </a>
          </div>

          {/* Burger menu */}
          <div className="lg:hidden flex">
            <button className="block" onClick={() => handleClick()}>
              {/* TODO Add some transition between X and burger */}
              {navOpen ? (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  width={32}
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                  className={`${scrolledDown ? "" : "fill-current"}`}
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M6 18L18 6M6 6l12 12"
                  />
                </svg>
              ) : (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  width={32}
                  stroke="currentColor"
                  className={`${scrolledDown ? "" : "fill-current text-white"}`}
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M4 6h16M4 12h16M4 18h16"
                  />
                </svg>
              )}
            </button>
            <button className="block" onClick={() => handleClick()}>
              {/* TODO Add some transition between X and burger */}
              {navOpen ? (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  width={32}
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                  className={`${scrolledDown ? "" : "fill-current"}`}
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M6 18L18 6M6 6l12 12"
                  />
                </svg>
              ) : (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  width={32}
                  stroke="currentColor"
                  className={`${scrolledDown ? "" : "fill-current text-white"}`}
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M4 6h16M4 12h16M4 18h16"
                  />
                </svg>
              )}
            </button>
          </div>
        </div>

        <div
          className={`
          ${navOpen ? "block text-white bg-dropdown" : "hidden lg:block"}
          ${scrolledDown && "bg-white"}`}
        >
          {/* List */}
          <ul
            className={`lg:flex lg:justify-center uppercase py-5 paddingx 
            ${navOpen ? "divide-y divide-dropdownborder" : "font-bold"}`}
          >
            <li className="navoptions">
              <a href="#">Hjem</a>
            </li>
            <li className="navoptions">
              <a href="#">Om oss</a>
            </li>
            <li className="navoptions">
              <a href="#">Tilbud</a>
            </li>
            <li className="navoptions">
              <a href="#">Meny</a>
            </li>
            <li className="navoptions">
              <a href="#">Kontakt oss</a>
            </li>
          </ul>
          <ul className>
            <li>fb</li>
            <li>fb</li>
            <li>fb</li>
          </ul>
        </div>
      </nav>
      <Banner />

      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
