import type { AppProps } from "next/app";
import "../styles/globals.css";
import { useState } from "react";

export default function App({ Component, pageProps }: AppProps) {
  const [nav, setNav] = useState(false);

  const handleClick = () => {
    if (nav) setNav(false);
    else setNav(true);
  };
  return (
    <div>
      <nav className="bg-gray-500">
        <div className="flex justify-between items-center bg-gray-500 pl-10 pr-10 pt-10 bg-green-300 lg:justify-center">
          {/* Title */}
          <div className="bg-pink-600">
            <a href="/" className="text-3xl uppercase bg-yellow-900">
              Lorem ipsum
            </a>
          </div>

          {/* Burger menu */}
          <div className="lg:hidden">
            <button className="bg-gray-400 block" onClick={() => handleClick()}>
              <svg className="h-6 w-6 fill-current" viewBox="0 0 24 24">
                {/* <path
                v-if="isOpen"
                fill-rule="evenodd"
                d="M18.278 16.864a1 1 0 0 1-1.414 1.414l-4.829-4.828-4.828 4.828a1 1 0 0 1-1.414-1.414l4.828-4.829-4.828-4.828a1 1 0 0 1 1.414-1.414l4.829 4.828 4.828-4.828a1 1 0 1 1 1.414 1.414l-4.828 4.829 4.828 4.828z"
              /> */}
                <path
                  fill-rule="evenodd"
                  d="M4 5h16a1 1 0 0 1 0 2H4a1 1 0 1 1 0-2zm0 6h16a1 1 0 0 1 0 2H4a1 1 0 0 1 0-2zm0 6h16a1 1 0 0 1 0 2H4a1 1 0 0 1 0-2z"
                />
              </svg>
            </button>
          </div>
        </div>

        <div>
          {/* List */}
          <ul
            className={`${
              nav ? "block" : "hidden"
            } bg-indigo-600 lg:flex lg:justify-center`}
          >
            <li className="px-5 bg-red-600 block">
              <a href="#">1</a>
            </li>
            <li className="px-14 bg-green-600 block">
              <a href="#">2</a>
            </li>
            <li className="px-14 bg-blue-600 block">
              <a href="#">3</a>
            </li>
            <li className="px-14 bg-yellow-600 block">
              <a href="#">4</a>
            </li>
            <li className="px-14 bg-purple-600 block">
              <a href="#">5</a>
            </li>
          </ul>
        </div>
      </nav>
    </div>
  );
}
